import classes from '../OrderItem/OrderItem.module.css';

const OrderItem = (props) => {
   const userDescription = `${classes.description} ${classes['user-detail']}`
   return (
      <div className={classes.order}>
         <h3>Order Number: {props.orderNr}</h3>
         <div>
            <ul className={userDescription}>
               <li>Name: {props.user.name}</li>
               <li>Street: {props.user.street}</li>
               <li>Post code: {props.user.postCode}</li>
               <li>City: {props.user.city}</li>
            </ul>
            <ul className={classes.description}>
               {props.items.map(item => {
                  const orderAmount = (item.price * item.amount).toFixed(2);
                  return <li>
                     <span>{item.amount}x</span>
                     <span>{item.name}</span>
                     <span className={classes.price}>Â£{orderAmount}</span>
                  </li>
               })}
            </ul>
         </div>
      </div>
   );
};

export default OrderItem;
